import React, { useState, useReducer } from "react";


function reducer(state, action) {
  switch (action.type) {
    case "ADD":
      return ({...state, todoList:[...todoList, action.payload.task] ,note:action.payload.note});
      default: return state
    }
}

const initialState = {
  todoList: [],
  note: "",
};

const App = () => {

  const [task, setTask] = useState("");
  const [taskList, setTaskList] = useState([])
  const [note, setNote] = useState("");
  const [showNotification, setShowNofication] = useState(true)
  const [state, dispatch] = useReducer(reducer, initialState);

  function submitHandler(e){
     e.preventDefault();
    //  setTaskList([...taskList,{todoList:task, note:note, id:Date.now()}]);
     dispatch({type:'ADD', payload:{task:task, note:note}})
     setTask('');
     setNote('');
  }
  console.log(state.todoList);
  // console.log(state);
  // console.log(taskList)
  return (
    <>
      <h1 className="text-5xl flex align-text-top justify-center my-10">
        To-Do List App
      </h1>
      <div className=" flex justify-center">
        <form className="flex flex-col bg-slate-500 p-5 w-96 h-96 rounded-2xl " onSubmit={submitHandler}>
          <label htmlFor="task" className="text-2xl font-semibold">Task</label>
          <input type="text" id="task" className="border-2 my-2 w-84 h-10 p-1"placeholder="Enter the task..."  
          value={task} onChange={(e)=>setTask(e.target.value)}/>
          <br />

          <label htmlFor="task" className="text-2xl font-semibold">Add Note</label>
          <textarea className="border-2 my-2 w-84 h-28 p-1 resize-none" placeholder="Add Note about task..."  
          value={note} onChange={(e)=>setNote(e.target.value)}/>
          <br />
          <button className="bg-green-800 text-yellow-500 w-32 h-10 rounded-xl">
            Add to List
          </button>
        </form>
      </div>
       <div>
         {state.todoList.map((task,index)=>(
           <div key={index}>
             {task}
          </div>
         ))}
       </div>

    </>
  );
};

export default App;
